import './App.css';
import React from 'react';
import { useState, useEffect } from 'react';


export default function WeatherApp() {
  const BASE_ATX_URL = "https://api.open-meteo.com/v1/forecast?latitude=30.27&longitude=-97.74&hourly=temperature_2m"
  const BASE_DAL_URL = "https://api.open-meteo.com/v1/forecast?latitude=32.78&longitude=-96.81&hourly=temperature_2m"
  const BASE_HOU_URL = "https://api.open-meteo.com/v1/forecast?latitude=29.76&longitude=-95.36&hourly=temperature_2m"
  const GEO_CODING_STUB = "https://geocoding-api.open-meteo.com/v1/search?name="
  const [time, setTime] = useState([]);
  const [temp, setTemp] = useState([]);
  let displayData


  async function pullJson(URL) {
    const response = await fetch(URL)
    const responseData = await response.json()
    let timeArr = responseData.hourly.time
    let tempArr = responseData.hourly.temperature_2m
    setTime(timeArr);
    setTemp(tempArr);
  }

  useEffect(() => {
    pullJson(BASE_ATX_URL)
  }, []);

  function onClickATX() {
    pullJson(BASE_ATX_URL)
  }

  function onClickDAL() {
    pullJson(BASE_DAL_URL)
  }

  function onClickHOU() {
    pullJson(BASE_HOU_URL)
  }

  return (<>
    <h2> Hello World </h2>
    <div class="container">
      {/* <CityInput /> */}
      <ThreeCityButtons />
      <ForecastHeader />
      < DataSet />
    </div>
    <h2> Goodbye</h2>
  </>)

  // function CityInput() {
  //   return (
  //     <p> Hellooo?</p>
  //     // <form onSubmit={getCustomCity()} >
  //     //   <input type="text" id="searchCity" name= "city" value="Enter City... " />
  //     //   <input type="submit" value="Submit" />
  //     // </form>
  //   )
  // }

  // function getCustomCity() {
  //   const inputCity = document.getElementById("searchCity").innerHTML
  //   console.log("input city is: ")
  // }

  function ThreeCityButtons() {
    return (
      <div class="row">
        <div class="col">
          <button id="ATX-btn" onClick={onClickATX}> <b> Austin </b> </button>
        </div>
        <div class="col">
          <button id="DAL-btn" onClick={onClickDAL}> <b> Dallas </b> </button>
        </div>
        <div class="col">
          <button id="HOU-btn" onClick={onClickHOU}> <b> Houston </b> </button>
        </div>
      </div>
    )
  }

  function ForecastHeader() {
    return (
      <div class="row">
        <div class="col-4">
          <b>Time</b>
        </div>
        <div class="col">
          <b>Temperature</b>
        </div>
      </div>
    )
  }
  function DataRow({ idx }) {
    return (
      <div class="row">
        <div class="col-4">
          {time[idx].substring(11)}
        </div>
        <div class="col">
          {temp[idx]} F
        </div>
      </div>
    )
  }

  function DataSet() {
    const returnValue = [];
    for (let i = 13; i < 24; i++) {
      returnValue.push(<DataRow idx={i} />)
    }
    return returnValue
  }

}
